jQuery( document ).ready( function($) {

	// Image as switcher background.
	$( document ).on( {
		mouseenter: function(e) {
			e.preventDefault();
			
			$( this ).parent().find( '.dipl_background_switcher_item' ).removeClass( 'dipl-bg-switcher-hover' );
			$( this ).addClass( 'dipl-bg-switcher-hover' );
			$( this ).find( '.dipl_bg_switcher_hover_content' ).slideDown();
		},
		mouseleave: function(e) {
			e.preventDefault();
			$( this ).find( '.dipl_bg_switcher_hover_content' ).slideUp( 'slow' );
		}
	}, '.dipl_background_switcher_item' );

} ); // Over Document ready.
