jQuery(document).ready( function ($) {
	// Smooth scroll on TOC link click
	$( '.dipl-table-of-content-wrapper a[href^="#"]' ).on( 'click', function(e) {
		e.preventDefault();

		var target = this.getAttribute( 'href' );
		if ( target && $(target).length ) {
			$( 'html, body' ).animate(
				{  scrollTop: $(target).offset().top - 50 }, // adjust 50 for header height
				600 // duration in ms
			);
		}
	} );
} );
