jQuery( document ).ready( function($) {

    let $winWidth = $(window).width();

    if ( $( document ).find( '.dipl_woo_products_categories_carousel' ).length > 0 ) {
        $( document ).find( '.dipl_woo_products_categories_carousel' ).each( function() {
            let $arrows = $(this).find( '.dipl_swiper_navigation' ).data();
            if ( $arrows ) {
                if ( $winWidth > 980 && typeof($arrows['arrows_desktop']) !== 'undefined' ) {
                    dipl_remove_arrows_classes( $(this).find( '.dipl_swiper_navigation' ) );
                    $(this).find('.dipl_swiper_navigation').addClass( 'dipl_arrows_' + $arrows['arrows_desktop'] );
                }
                if ( $winWidth < 981 ) {
                    if ( typeof($arrows['arrows_tablet']) !== 'undefined' ) {
                        dipl_remove_arrows_classes( $(this).find( '.dipl_swiper_navigation' ) );
                        $(this).find('.dipl_swiper_navigation').addClass( 'dipl_arrows_' + $arrows['arrows_tablet'] );
                    } else if ( typeof($arrows['arrows_desktop']) !== 'undefined' ) {
                        dipl_remove_arrows_classes( $(this).find( '.dipl_swiper_navigation' ) );
                        $(this).find('.dipl_swiper_navigation').addClass( 'dipl_arrows_' + $arrows['arrows_desktop'] );
                    }
                }
                if ( $winWidth < 768 ) {
                    if ( typeof($arrows['arrows_phone']) !== 'undefined' ) {
                        dipl_remove_arrows_classes( $(this).find( '.dipl_swiper_navigation' ) );
                        $(this).find('.dipl_swiper_navigation').addClass( 'dipl_arrows_' + $arrows['arrows_phone'] );
                    } else if ( typeof($arrows['arrows_tablet']) !== 'undefined' ) {
                        dipl_remove_arrows_classes( $(this).find( '.dipl_swiper_navigation' ) );
                        $(this).find('.dipl_swiper_navigation').addClass( 'dipl_arrows_' + $arrows['arrows_tablet'] );
                    } else if ( typeof($arrows['arrows_desktop']) !== 'undefined' ) {
                        dipl_remove_arrows_classes( $(this).find( '.dipl_swiper_navigation' ) );
                        $(this).find('.dipl_swiper_navigation').addClass( 'dipl_arrows_' + $arrows['arrows_desktop'] );
                    }
                }
            }
        } );
    }

    $(window).resize( function() {
        let $winWidth = $(window).width();
        if ( $( document ).find( '.dipl_woo_products_categories_carousel' ).length > 0 ) {
            $( document ).find( '.dipl_woo_products_categories_carousel' ).each( function() {
                let $arrows = $(this).find('.dipl_swiper_navigation').data();
                if ( $arrows ) {
                    if ( $winWidth > 980 && typeof($arrows['arrows_desktop']) !== 'undefined' ) {
                        dipl_remove_arrows_classes( $(this).find( '.dipl_swiper_navigation' ) );
                        $(this).find('.dipl_swiper_navigation').addClass( 'dipl_arrows_' + $arrows['arrows_desktop'] );
                    }
                    if ( $winWidth < 981 ) {
                        if ( typeof($arrows['arrows_tablet']) !== 'undefined' ) {
                            dipl_remove_arrows_classes( $(this).find( '.dipl_swiper_navigation' ) );
                            $(this).find('.dipl_swiper_navigation').addClass( 'dipl_arrows_' + $arrows['arrows_tablet'] );
                        } else if ( typeof($arrows['arrows_desktop']) !== 'undefined' ) {
                            dipl_remove_arrows_classes( $(this).find( '.dipl_swiper_navigation' ) );
                            $(this).find('.dipl_swiper_navigation').addClass( 'dipl_arrows_' + $arrows['arrows_desktop'] );
                        }
                    }
                    if ( $winWidth < 768 ) {
                        if ( typeof($arrows['arrows_phone']) !== 'undefined' ) {
                            dipl_remove_arrows_classes( $(this).find('.dipl_swiper_navigation') );
                            $(this).find('.dipl_swiper_navigation').addClass( 'dipl_arrows_' + $arrows['arrows_phone'] );
                        } else if ( typeof($arrows['arrows_tablet']) !== 'undefined' ) {
                            dipl_remove_arrows_classes( $(this).find('.dipl_swiper_navigation') );
                            $(this).find('.dipl_swiper_navigation').addClass( 'dipl_arrows_' + $arrows['arrows_tablet'] );
                        } else if ( typeof($arrows['arrows_desktop']) !== 'undefined' ) {
                            dipl_remove_arrows_classes( $(this).find('.dipl_swiper_navigation') );
                            $(this).find('.dipl_swiper_navigation').addClass( 'dipl_arrows_' + $arrows['arrows_desktop'] );
                        }
                    }
                }
            } );
        }
    });

    function dipl_get_arrows_classes() {
        return [
            'dipl_arrows_top_left', 'dipl_arrows_top_center', 'dipl_arrows_top_right',
            'dipl_arrows_bottom_left', 'dipl_arrows_bottom_center', 'dipl_arrows_bottom_right',
            'dipl_arrows_outside', 'dipl_arrows_inside',
        ];
    }

    function dipl_remove_arrows_classes( $element ) {
        let $arrowClasses = dipl_get_arrows_classes();
        $element.removeClass( $arrowClasses.join(' ') );
    }
} ); // Document ready.
